// Extract receipt data from OCR text
const ocrText = $input.first().json.textAnnotations?.[0]?.description || '';
const lines = ocrText.split('\n').filter(line => line.trim());

// Extract vendor name (usually first meaningful line)
let vendor = '';
for (const line of lines) {
  if (line.length > 3 && !line.match(/^[0-9\s\$\.,]+$/)) {
    vendor = line.trim();
    break;
  }
}

// Extract amount (look for currency patterns)
const amountMatches = ocrText.match(/\$?([0-9,]+\.?[0-9]*)/g);
let amount = 0;
if (amountMatches) {
  // Take the largest amount found (likely the total)
  const amounts = amountMatches.map(match => 
    parseFloat(match.replace(/[\$,]/g, ''))
  ).filter(num => !isNaN(num));
  amount = Math.max(...amounts);
}

// Extract date (various formats)
const datePatterns = [
  /([0-9]{1,2}[\/\-][0-9]{1,2}[\/\-][0-9]{2,4})/,
  /([0-9]{4}[\/\-][0-9]{1,2}[\/\-][0-9]{1,2})/,
  /(Jan|Feb|Mar|Apr|May|Jun|Jul|Aug|Sep|Oct|Nov|Dec)[a-z]*\s+[0-9]{1,2},?\s+[0-9]{4}/i
];

let receiptDate = new Date().toISOString().split('T')[0]; // Default to today
for (const pattern of datePatterns) {
  const match = ocrText.match(pattern);
  if (match) {
    const parsedDate = new Date(match[1]);
    if (!isNaN(parsedDate.getTime())) {
      receiptDate = parsedDate.toISOString().split('T')[0];
      break;
    }
  }
}

// Determine category based on vendor name
let category = 'Office Expenses'; // Default
const vendorLower = vendor.toLowerCase();
if (vendorLower.includes('restaurant') || vendorLower.includes('cafe') || vendorLower.includes('food')) {
  category = 'Meals & Entertainment';
} else if (vendorLower.includes('gas') || vendorLower.includes('fuel') || vendorLower.includes('shell') || vendorLower.includes('exxon')) {
  category = 'Travel & Vehicle';
} else if (vendorLower.includes('hotel') || vendorLower.includes('airline') || vendorLower.includes('uber') || vendorLower.includes('lyft')) {
  category = 'Travel & Entertainment';
} else if (vendorLower.includes('office') || vendorLower.includes('supply') || vendorLower.includes('staples')) {
  category = 'Office Supplies';
}

return [{
  vendor_name: vendor,
  amount: amount,
  transaction_date: receiptDate,
  category: category,
  raw_ocr_text: ocrText,
  confidence: vendor && amount > 0 ? 0.8 : 0.3
}];

// Vendor matching logic with fuzzy search
const extractedVendor = $node['Extract Receipt Data'].json.vendor_name.toLowerCase();
const qboVendors = $input.first().json || [];

// Levenshtein distance function
function levenshteinDistance(str1, str2) {
  const matrix = [];
  for (let i = 0; i <= str2.length; i++) {
    matrix[i] = [i];
  }
  for (let j = 0; j <= str1.length; j++) {
    matrix[0][j] = j;
  }
  for (let i = 1; i <= str2.length; i++) {
    for (let j = 1; j <= str1.length; j++) {
      if (str2.charAt(i - 1) === str1.charAt(j - 1)) {
        matrix[i][j] = matrix[i - 1][j - 1];
      } else {
        matrix[i][j] = Math.min(
          matrix[i - 1][j - 1] + 1,
          matrix[i][j - 1] + 1,
          matrix[i - 1][j] + 1
        );
      }
    }
  }
  return matrix[str2.length][str1.length];
}

// Calculate similarity score
function calculateSimilarity(str1, str2) {
  const longer = str1.length > str2.length ? str1 : str2;
  const shorter = str1.length > str2.length ? str2 : str1;
  if (longer.length === 0) return 1.0;
  const editDistance = levenshteinDistance(longer, shorter);
  return (longer.length - editDistance) / longer.length;
}

// Find best match
let bestMatch = null;
let bestScore = 0;

for (const vendor of qboVendors) {
  const vendorName = vendor.Name.toLowerCase();

  // Check exact match first
  if (vendorName === extractedVendor) {
    bestMatch = vendor;
    bestScore = 1.0;
    break;
  }

  // Check partial matches
  if (vendorName.includes(extractedVendor) || extractedVendor.includes(vendorName)) {
    const score = 0.9;
    if (score > bestScore) {
      bestMatch = vendor;
      bestScore = score;
    }
  }

  // Fuzzy matching
  const similarity = calculateSimilarity(extractedVendor, vendorName);
  if (similarity > bestScore && similarity > 0.7) {
    bestMatch = vendor;
    bestScore = similarity;
  }
}

return [{
  matched_vendor: bestMatch,
  confidence_score: bestScore,
  should_create_new: bestScore < 0.8,
  extracted_vendor: extractedVendor
}];

